	//Location is outside the region or not 
	public boolean isInCMC(LatLng location) {
		if (location == null)
			return false;

		List<LatLng> polyLoc = getCMCRegion();
		
		LatLng lastPoint = polyLoc.get(polyLoc.size() - 1);
		boolean isInside = false;
		double x = location.longitude;

		for (LatLng point : polyLoc) {
			double x1 = lastPoint.longitude;
			double x2 = point.longitude;
			double dx = x2 - x1;

			if (Math.abs(dx) > 180.0) {
				// we have, most likely, just jumped the dateline (could do further validation to this effect if needed).  normalise the numbers.
				if (x > 0) {
					while (x1 < 0)
						x1 += 360;
					while (x2 < 0)
						x2 += 360;
				} else {
					while (x1 > 0)
						x1 -= 360;
					while (x2 > 0)
						x2 -= 360;
				}
				dx = x2 - x1;
			}

			if ((x1 <= x && x2 > x) || (x1 >= x && x2 < x)) {
				double grad = (point.latitude - lastPoint.latitude) / dx;
				double intersectAtLat = lastPoint.latitude + ((x - x1) * grad);

				if (intersectAtLat > location.latitude)
					isInside = !isInside;
			}
			lastPoint = point;
		}

		return isInside;
	}

