function set_boundaries(){ 
    global $boundary;

    for($i = 0; $i < count($boundary); $i++){
        for($j = 0; $j < count($boundary[$i]); $j++){
             for($k = 0; $k < count($boundary[$i][$j]); $k++){
                 while($boundary[$i][$j][$k] > 360.0) $boundary[$i][$j][$k] -= 360.0;
             }	
        } 	
    }
}

function check_ward($points, $x, $y){
    $result = false;
    for ($i = 0, $j = count($points) - 1; $i < count($points); $j = $i++){
        if (($points[$i][1] > $y) != ($points[$j][1] > $y) and ($x < ($points[$j][0] - $points[$i][0]) * ($y - $points[$i][1]) / ($points[$j][1] - $points[$i][1]) + $points[$i][0])) {
            $result = !$result;
        }
    }
    return $result;
}

function get_ward($x, $y){
   global $boundary;
   for($i = 0; $i < count($boundary); $i++){
       if(check_ward($boundary[$i], $x, $y)) return $i + 1;
   }
   return 0;
}



